// Apply white balance filter
function applyWhiteBalance() {
  if (!video) return;
  
  // Small delay to ensure video is ready
  setTimeout(() => {
    const mode = WHITE_BALANCE_MODES[currentWhiteBalanceIndex];
    
    // Remove existing filter
    video.style.filter = '';
    
    // Apply CSS filter based on mode
    switch(mode.value) {
      case 'daylight':
        video.style.filter = 'brightness(1.05) saturate(1.1)';
        break;
      case 'cloudy':
        video.style.filter = 'brightness(1.1) saturate(0.95) sepia(0.05)';
        break;
      case 'tungsten':
        video.style.filter = 'brightness(0.95) saturate(1.15) hue-rotate(-10deg)';
        break;
      case 'fluorescent':
        video.style.filter = 'brightness(1.02) saturate(1.05) hue-rotate(5deg)';
        break;
      case 'candlelight':
        video.style.filter = 'brightness(0.85) saturate(1.3) sepia(0.15) hue-rotate(-15deg)';
        break;
      case 'moonlight':
        video.style.filter = 'brightness(0.7) saturate(0.8) hue-rotate(15deg) contrast(1.1)';
        break;
      case 'auto':
      default:
        video.style.filter = '';
        break;
    }
  }, 50);
}

function applyWhiteBalanceToCanvas(ctx, width, height) {
  const mode = WHITE_BALANCE_MODES[currentWhiteBalanceIndex];
  
  if (mode.value === 'auto') {
    return; // No adjustment needed
  }
  
  // Get image data
  const imageData = ctx.getImageData(0, 0, width, height);
  const data = imageData.data;
  
  // Define adjustments for each mode
  let brightness = 1.0;
  let saturation = 1.0;
  let warmth = 0; // Positive = warmer (red/yellow), Negative = cooler (blue)
  let contrast = 1.0;
  
  switch(mode.value) {
    case 'daylight':
      brightness = 1.05;
      saturation = 1.1;
      warmth = 5;
      break;
    case 'cloudy':
      brightness = 1.1;
      saturation = 0.95;
      warmth = 10;
      break;
    case 'tungsten':
      brightness = 0.95;
      saturation = 1.15;
      warmth = -20;
      break;
    case 'fluorescent':
      brightness = 1.02;
      saturation = 1.05;
      warmth = -10;
      break;
    case 'candlelight':
      brightness = 0.85;
      saturation = 1.3;
      warmth = 25;
      contrast = 0.95;
      break;
    case 'moonlight':
      brightness = 0.7;
      saturation = 0.8;
      warmth = -15;
      contrast = 1.1;
      break;
  }
  
  // Apply adjustments to each pixel
  for (let i = 0; i < data.length; i += 4) {
    let r = data[i];
    let g = data[i + 1];
    let b = data[i + 2];
    
    // Apply warmth (shift towards red/yellow or blue)
    if (warmth > 0) {
      r = Math.min(255, r + warmth);
      g = Math.min(255, g + warmth * 0.5);
    } else if (warmth < 0) {
      b = Math.min(255, b - warmth);
    }
    
    // Apply brightness
    r *= brightness;
    g *= brightness;
    b *= brightness;
    
    // Apply saturation
    const gray = 0.2989 * r + 0.5870 * g + 0.1140 * b;
    r = gray + saturation * (r - gray);
    g = gray + saturation * (g - gray);
    b = gray + saturation * (b - gray);
    
    // Apply contrast
    r = ((r / 255 - 0.5) * contrast + 0.5) * 255;
    g = ((g / 255 - 0.5) * contrast + 0.5) * 255;
    b = ((b / 255 - 0.5) * contrast + 0.5) * 255;
    
    // Clamp values
    data[i] = Math.max(0, Math.min(255, r));
    data[i + 1] = Math.max(0, Math.min(255, g));
    data[i + 2] = Math.max(0, Math.min(255, b));
  }
  
  // Put modified image data back
  ctx.putImageData(imageData, 0, 0);
}

